def test_case_exists(case: str) -> bool:
    df: DataFrame = spark.sql(f"select * from odw_curated_db.nsip_project where caseReference = '{case}'")
    return df.count() > 0

def test_case_exists_in_nsip_representation(case: str) -> bool:
    df: DataFrame = spark.sql(f"select * from odw_curated_db.nsip_representation where caseRef = '{case}'")
    return df.count() > 0

def test_reps_counts_match(query1: str, query2: str) -> tuple:
    df1 = spark.sql(query1)
    df2 = spark.sql(query2)
    return (df1.count() == df2.count(), df1.count(), df2.count())
