def test_case_exists(case: str) -> bool:
    df: DataFrame = spark.sql(f"select * from odw_curated_db.nsip_data where caseReference = '{case}'")
    return df.count() > 0

def test_case_exists_in_nsip_representation(case: str) -> bool:
    df: DataFrame = spark.sql(f"select * from odw_curated_db.nsip_representation where caseRef = '{case}'")
    return df.count() > 0
