from pyspark.sql.functions import udf
from pyspark.sql.types import DateType
from datetime import datetime

def safe_to_date(date_str, date_format):
    try:
        return datetime.strptime(date_str, date_format).date()
    except:
        return None

safe_to_date_udf = udf(safe_to_date, DateType())

spark.udf.register("try_to_date", safe_to_date_udf)

# Now you can use try_to_date in your SQL queries
spark.sql("SELECT try_to_date(StartDate, 'yyyy-MM-dd') AS StartDate FROM odw_standardised_db.inspector_addresses_monthly  where StartDate is not null").show()

