#!/usr/bin/env python
# coding: utf-8

# ## py_utils_common_raw_standardised
# 
# The purpose of this pyspark notebook is to reads all recent files from the given odw-raw folder path and load the data into standardised_db lakehouse database's Delta tables

# Author               Created Date              Description
# Rohit Shukla         19-Jan-2025             The functionality of this notebook is generic to cater to .xlsx and .csv files for creating Delta Tables.
# Enhanced             [Current Date]          Added functionality for multi-file processing with date-based ordering and MERGE schema support

# Spark Cluster Configuration -> Apache Spark Version- 3.4, Python Version - 3.10, Delta Lake Version - 2.4

# The input parameters are:
# Param_FileFolder_Path => This is a mandatory parameter which refers to a folder path of the entities like 'Timesheets', 'SapHrData'
# Param_File_Load_Type => This is an optional parameter refers to a subfolders if there is any like Monthly,Daily, Quarterly etc.
# Param_Json_SchemaFolder_Name => This is a mandatory parameter which refers to a schema file in json format required to create delta tables.


# Get Storage account
#Get Storage account name
storage_account=mssparkutils.notebook.run('/utils/py_utils_get_storage_account')
#print(storage_account)



# Define all required folder paths
# Define all Folder paths used in the notebook

Param_Json_SchemaFolder_Name = Param_FileFolder_Path.lower()

odw_raw_base_folder_path = f"abfss://odw-raw@{storage_account}{Param_FileFolder_Path}/"
delta_table_base_path = f"abfss://odw-standardised@{storage_account}{Param_Json_SchemaFolder_Name}"
json_schema_file_path = f"abfss://odw-config@{storage_account}/orchestration/orchestration_saphr.json"

database_name = "odw_standardised_db"
process_name = 'py_raw_to_std'

logging_container = f"abfss://logging@{storage_account}"
logging_table_name = 'tables_logs'
ingestion_log_table_location = logging_container + logging_table_name

# json result result dump list
processing_results = []

# --- Load Orchestration Config ---
df_orch = spark.read.option("multiline", "true").json(json_schema_file_path)
definitions = json.loads(df_orch.toJSON().first())["definitions"]

# Find the recent Date subfolder path and construct the odw-raw path dynamically
# Get latest folder
def get_latest_folder(path):
    folders = [f.name for f in mssparkutils.fs.ls(path) if f.isDir]
    folders = sorted([f for f in folders if re.match(r"\d{4}-\d{2}-\d{2}", f)], reverse=True)
    return folders[0] if folders else None

# ENHANCED: Function to check if filename has date pattern
def has_date_pattern_in_filename(filename):
    """Check if filename contains a date pattern"""
    date_patterns = [
        r'_\d{8}',           # _YYYYMMDD
        r'_\d{4}-\d{2}-\d{2}', # _YYYY-MM-DD
        r'_\d{4}_\d{2}_\d{2}', # _YYYY_MM_DD
        r'\d{8}',            # YYYYMMDD (anywhere in filename)
        r'\d{4}-\d{2}-\d{2}', # YYYY-MM-DD (anywhere in filename)
        r'\d{4}_\d{2}_\d{2}'  # YYYY_MM_DD (anywhere in filename)
    ]

    for pattern in date_patterns:
        if re.search(pattern, filename):
            return True
    return False

# ENHANCED: Function to find files with same base name but different dates
def find_multi_date_files(files, filename_start):
    """Find files that have the same base name but different dates"""
    matching_files = []
    base_files = []

    for file in files:
        if file.startswith(filename_start):
            if has_date_pattern_in_filename(file):
                matching_files.append(file)
            else:
                base_files.append(file)

    # If we found files with date patterns, use those for multi-file processing
    if len(matching_files) > 1:
        return matching_files, 'multi_file'
    elif len(matching_files) == 1:
        return matching_files, 'single_file'
    elif len(base_files) == 1:
        # Fallback to single file without date pattern
        return base_files, 'single_file'
    elif len(base_files) > 1:
        # Multiple files without clear date pattern - take the first one
        return [base_files[0]], 'single_file'
    else:
        return [], 'none'

# ENHANCED: Function to extract date from filename with various date formats
def extract_date_from_filename(filename):
    """Extract date from filename supporting multiple date formats"""
    try:
        # Pattern for YYYYMMDD format (8 digits)
        date_pattern_8 = r'(\d{8})'
        # Pattern for YYYY-MM-DD format
        date_pattern_dash = r'(\d{4}-\d{2}-\d{2})'
        # Pattern for YYYY_MM_DD format
        date_pattern_underscore = r'(\d{4}_\d{2}_\d{2})'

        match_8 = re.search(date_pattern_8, filename)
        match_dash = re.search(date_pattern_dash, filename)
        match_underscore = re.search(date_pattern_underscore, filename)

        if match_8:
            date_str = match_8.group(1)
            return datetime.strptime(date_str, '%Y%m%d')
        elif match_dash:
            date_str = match_dash.group(1)
            return datetime.strptime(date_str, '%Y-%m-%d')
        elif match_underscore:
            date_str = match_underscore.group(1)
            return datetime.strptime(date_str, '%Y_%m_%d')
        else:
            logInfo(f"No date found in filename: {filename}")
            return datetime.min
    except Exception as e:
        logError(f"Error extracting date from filename {filename}: {e}")
        return datetime.min

# ENHANCED: Function to check schema compatibility and determine write mode
def determine_write_strategy(spark_df, delta_table_path, schema_path, table_name):
    """Determine the appropriate write strategy based on schema compatibility"""
    try:
        # Load expected schema from JSON
        schema_json = json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))
        expected_fields = {f['name'].lower(): f['type'] for f in schema_json["fields"]}

        # Get actual DataFrame schema (excluding metadata columns we always add)
        metadata_columns = {'ingested_datetime', 'expected_from', 'expected_to'}
        actual_fields = {field.name.lower(): str(field.dataType) for field in spark_df.schema.fields if field.name.lower() not in metadata_columns}

        # Check if Delta table exists
        table_exists = DeltaTable.isDeltaTable(spark, delta_table_path)

        if not table_exists:
            logInfo(f"Delta table does not exist for {table_name}. Will create new table.")
            return "create_new", None

        # If table exists, get its current schema (excluding old tracking columns)
        try:
            current_table_df = spark.read.format("delta").load(delta_table_path)
            # Exclude old tracking columns and metadata columns from comparison
            excluded_columns = {'source_file', 'file_processed_datetime', 'ingested_datetime', 'expected_from', 'expected_to'}
            current_fields = {field.name.lower(): str(field.dataType) for field in current_table_df.schema.fields if field.name.lower() not in excluded_columns}
        except Exception as e:
            logError(f"Error reading existing table schema: {e}")
            return "create_new", None

        # Compare only business data columns (not metadata or tracking columns)
        missing_in_current = set(actual_fields.keys()) - set(current_fields.keys())
        missing_in_actual = set(current_fields.keys()) - set(actual_fields.keys())

        if missing_in_current and not missing_in_actual:
            logInfo(f"New business columns detected in source data: {missing_in_current}")
            logInfo(f"Will use MERGE schema option for {table_name}")
            return "merge_schema", missing_in_current
        elif not missing_in_current and not missing_in_actual:
            logInfo(f"Business schema matches exactly for {table_name}. Will use standard overwrite/append.")
            return "overwrite", None
        elif missing_in_actual and not missing_in_current:
            logInfo(f"Some columns missing in source data for {table_name}: {missing_in_actual}")
            logInfo(f"Will use standard overwrite/append - missing columns will be null.")
            return "overwrite", None
        else:
            logInfo(f"Schema differences detected for {table_name}")
            logInfo(f"Missing in current table: {missing_in_current}")
            logInfo(f"Missing in source data: {missing_in_actual}")
            logInfo(f"Will use standard overwrite/append")
            return "overwrite", None

    except Exception as e:
        logError(f"Error determining write strategy for {table_name}: {e}")
        return "overwrite", None

# ENHANCED: Function to check if JSON schema has evolved vs existing Delta table
def check_json_schema_evolution(delta_table_path, schema_path, table_name):
    """Check if JSON schema has evolved compared to existing Delta table schema"""
    try:
        # Check if Delta table exists
        table_exists = DeltaTable.isDeltaTable(spark, delta_table_path)

        if not table_exists:
            logInfo(f"Delta table does not exist for {table_name}. Will create new table.")
            return False, "create_new"

        # Get JSON schema
        schema_json = json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))
        expected_fields = set([f['name'] for f in schema_json["fields"]])

        # Get existing table schema
        try:
            existing_table_df = spark.read.format("delta").load(delta_table_path)
            # Remove metadata columns from comparison
            metadata_columns = {"ingested_datetime", "expected_from", "expected_to"}
            existing_business_fields = set(existing_table_df.columns) - metadata_columns
            expected_business_fields = expected_fields - metadata_columns

            # Check for schema differences between JSON and existing table
            if existing_business_fields != expected_business_fields:
                missing_in_table = expected_business_fields - existing_business_fields
                missing_in_json = existing_business_fields - expected_business_fields

                logInfo(f"JSON schema evolution detected for {table_name}:")
                logInfo(f"  Columns in JSON schema but not in existing table: {missing_in_table}")
                logInfo(f"  Columns in existing table but not in JSON schema: {missing_in_json}")

                if missing_in_json:
                    # Columns have been removed from JSON schema - need schema reset
                    logInfo(f"JSON schema has removed columns. Will use schema reset for {table_name}")
                    return True, "schema_reset"
                elif missing_in_table:
                    # New columns added to JSON schema - can use merge schema
                    logInfo(f"JSON schema has new columns. Will use merge schema for {table_name}")
                    return False, "merge_schema"

            logInfo(f"JSON schema matches existing table exactly for {table_name}. Will use standard overwrite.")
            return False, "standard"

        except Exception as e:
            logError(f"Error reading existing table schema for {table_name}: {e}")
            logInfo(f"Will reset schema due to read error")
            return True, "schema_reset"

    except Exception as e:
        logError(f"Error checking JSON schema evolution for {table_name}: {e}")
        return False, "standard"

# ENHANCED: Function to write DataFrame based on JSON schema evolution
@logging_to_appins
def write_dataframe_with_json_schema_compliance(spark_df, delta_table_path, full_table_name, schema_path, is_first_file=True):
    """Write DataFrame with JSON schema compliance - JSON schema is the ultimate truth"""
    try:
        # Check if JSON schema has evolved compared to existing table
        needs_reset, strategy = check_json_schema_evolution(delta_table_path, schema_path, full_table_name)

        if needs_reset or strategy == "schema_reset":
            # JSON schema evolution requires table reset (handles column drops)
            create_table_if_not_exists(delta_table_path, full_table_name, schema_path)
            spark_df.write.format("delta").mode("overwrite").option("overwriteSchema", "true").save(delta_table_path)
            logInfo(f"Reset Delta table schema to match JSON schema: {full_table_name}")

        elif strategy == "merge_schema":
            # JSON schema has new columns - use merge schema
            create_table_if_not_exists(delta_table_path, full_table_name, schema_path)
            if is_first_file:
                spark_df.write.format("delta").mode("overwrite").option("mergeSchema", "true").save(delta_table_path)
                logInfo(f"Overwrote with schema merge to match JSON schema: {full_table_name}")
            else:
                spark_df.write.format("delta").mode("append").option("mergeSchema", "true").save(delta_table_path)
                logInfo(f"Appended with schema merge to match JSON schema: {full_table_name}")

        elif strategy == "create_new":
            # New table - create based on JSON schema
            create_table_if_not_exists(delta_table_path, full_table_name, schema_path)
            spark_df.write.format("delta").mode("overwrite").save(delta_table_path)
            logInfo(f"Created new Delta table based on JSON schema: {full_table_name}")

        else:
            # Standard write (strategy == "standard") - JSON schema matches existing table
            if is_first_file:
                spark_df.write.format("delta").mode("overwrite").save(delta_table_path)
                logInfo(f"Overwrote Delta table (JSON schema unchanged): {full_table_name}")
            else:
                spark_df.write.format("delta").mode("append").save(delta_table_path)
                logInfo(f"Appended to Delta table (JSON schema unchanged): {full_table_name}")

        return True

    except Exception as e:
        logError(f"Error writing DataFrame to {full_table_name}: {e}")
        return False

# Define all files and dataframe processing related functions
#Defining all functions

@logging_to_appins
def read_file(file_path):
    try:
        if file_path.endswith(".csv"):
            return spark.read.option("header", True).csv(file_path)

        elif file_path.endswith(".xlsx"):
            return spark.read.format("com.crealytics.spark.excel") \
                             .option("header", "true") \
                             .option("inferSchema", "true") \
                             .load(file_path)        
        else:
            raise Exception("Unsupported file format")
    except Exception as e:
        logError(f"Failed to load file {file_path}: {e}")
        return None

def clean_column_names(df):
    cols = [re.sub(r"[^a-zA-Z0-9_]+", "", c).strip('_') for c in df.columns]
    deduped = []
    for i, c in enumerate(cols):
        count = cols[:i].count(c)
        deduped.append(f"{c}{count + 1}" if count else c)
    return df.toDF(*deduped)

# Reorder dataframe columns to bring additional metadata columns to the front
def reorder_columns(df):
    """Reorders the columns so that metadata columns come first."""
    try:
        metadata_cols = ["ingested_datetime", "expected_from", "expected_to"]
        remaining_cols = [col for col in df.columns if col not in metadata_cols]
        return df.select(metadata_cols + remaining_cols)
    except Exception as e:
        logError(f"Error reordering columns: {e}")
        return df

@logging_to_appins
def create_table_if_not_exists(path, table_name, schema_path):
    try:
        if not DeltaTable.isDeltaTable(spark, path):
            schema_json = json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))
            schema_str = ", ".join([f"{f['name']} {f['type']}" for f in schema_json["fields"]])
            spark.sql(f"CREATE TABLE IF NOT EXISTS {table_name} ({schema_str}) USING DELTA LOCATION '{path}'")
    except Exception as e:
        logError(f"Error creating table {table_name}: {e}")

@logging_to_appins
def time_diff_seconds(start, end):
    try:
        if not start or not end:
            return 0

        # Parse strings into datetime objects if needed
        if isinstance(start, str):
            start = datetime.strptime(start, "%Y-%m-%d %H:%M:%S.%f")
        if isinstance(end, str):
            end = datetime.strptime(end, "%Y-%m-%d %H:%M:%S.%f")

        diff_seconds = int((end - start).total_seconds())
        return diff_seconds if diff_seconds > 0 else 0

    except Exception as e:
        return 0

#This funtion handles datetime object and covert into string
def datetime_handler(obj):
    if isinstance(obj, datetime):
        return obj.isoformat()
    raise TypeError(f"Object of type {obj.__class__.__name__} is not JSON serializable")

# ENHANCED: Main Delta Table Ingestion Logic with multi-file support
#-- Main Delta Table Ingestion Logic---

@logging_to_appins
def process_definitions(Param_File_Load_Type):
    matched_definitions = []
    unmatched_definitions = []
    all_latest_files = []

    # Filter only matched definitions
    for definition in definitions:

        freq_folder = definition.get('Source_Frequency_Folder', '').lower()
        source_folder = definition.get('Source_Folder', '').lower()
        param_freq = (Param_File_Load_Type or '').lower()
        param_path = (Param_FileFolder_Path or '').lower()

        if Param_File_Load_Type and not (
            freq_folder == param_freq and source_folder == param_path
        ):
            continue

        source_path = f"abfss://odw-raw@{storage_account}{Param_FileFolder_Path}/"

        if Param_File_Load_Type:            
            source_path += f"{Param_File_Load_Type}/"

        try:
            latest_folder = get_latest_folder(source_path)
            if not latest_folder:
                logInfo(f"No folders in path {source_path}")
                continue

            latest_path = f"{source_path}{latest_folder}/"
            files = [f.name for f in mssparkutils.fs.ls(latest_path) if not f.isDir]
            all_latest_files.extend(files)

            # ENHANCED: Automatically detect multi-file processing based on filename patterns
            filename_start = definition['Source_Filename_Start']

            # Find files with the same base name but different dates
            matching_files, processing_mode = find_multi_date_files(files, filename_start)

            logInfo(f"File analysis for '{filename_start}': found {len(matching_files)} files, mode: {processing_mode}")
            logInfo(f"Matching files: {matching_files}")

            if processing_mode == 'multi_file':
                # Multi-file processing mode - sort files by date
                logInfo(f"Enabling multi-file processing for {definition['Standardised_Table_Name']} - found {len(matching_files)} files with date patterns")

                # Sort files by date extracted from filename
                matching_files_with_dates = []
                for file in matching_files:
                    file_date = extract_date_from_filename(file)
                    matching_files_with_dates.append((file, file_date))

                # Sort by date (oldest first for sequential processing)
                matching_files_with_dates.sort(key=lambda x: x[1])
                sorted_files = [file for file, _ in matching_files_with_dates]

                logInfo(f"Files sorted by date: {sorted_files}")

                definition['matched_files'] = [f"{latest_path}{f}" for f in sorted_files]
                definition['latest_path'] = latest_path
                definition['processing_mode'] = 'multi_file'
                matched_definitions.append(definition)
                logInfo(f"Setup multi-file processing for {definition['Standardised_Table_Name']} with {len(sorted_files)} files")

            elif processing_mode == 'single_file':
                # Single file processing mode
                matching_file = matching_files[0]
                definition['matched_file'] = matching_file
                definition['latest_path'] = latest_path
                definition['processing_mode'] = 'single_file'
                matched_definitions.append(definition)
                logInfo(f"Setup single-file processing for {definition['Standardised_Table_Name']} with file: {matching_file}")

            else:
                # No matching files found
                unmatched_definitions.append(definition['Standardised_Table_Name'])
                logInfo(f"No files found matching pattern '{filename_start}' for {definition['Standardised_Table_Name']}")

        except Exception as e:
            logError(f"Could not read from {source_path}: {e}")

    #List filenames if nothing matched in Orchestration.json
    processed_files = set()
    for d in matched_definitions:
        if d['processing_mode'] == 'multi_file':
            processed_files.update([os.path.basename(f) for f in d['matched_files']])
        else:
            processed_files.add(d['matched_file'])

    unmatched_files = set(all_latest_files) - processed_files
    if unmatched_files:
        logError(f"Files found in source but not defined in orchestration.json: {', '.join(unmatched_files)}")

    # Step 3: Process each matched definition
    for definition in matched_definitions:

        if definition['processing_mode'] == 'multi_file':
            # Process each file individually and create separate entries
            file_index = 0
            table_created = False  # Track if table has been successfully created

            for file_path in definition['matched_files']:
                file_name = os.path.basename(file_path)

                # Create individual result entry for each file
                result_entry = {
                    "delta_table_name": definition['Standardised_Table_Name'],
                    "csv_file_name": file_name,
                    "record_count": 0,
                    "table_result": "failed",
                    "start_exec_time": "",
                    "end_exec_time": "",
                    "total_exec_time": "",
                    "error_message": ""
                }

                try:
                    start_exec_time = str(datetime.now())
                    result_entry["start_exec_time"] = start_exec_time

                    # Process individual file
                    sparkDF = read_file(file_path)
                    if sparkDF is None:
                        logError(f"No data loaded for file: {file_name}")
                        result_entry["error_message"] = f"No data loaded for file: {file_name}"
                        result_entry["end_exec_time"] = str(datetime.now())
                        result_entry["total_exec_time"] = str(time_diff_seconds(start_exec_time, datetime.now()))
                        processing_results.append(result_entry)
                        file_index += 1
                        continue

                    expected_from = datetime.now() - timedelta(days=definition['Expected_Within_Weekdays'])
                    expected_to = datetime.now()

                    sparkDF = clean_column_names(sparkDF)

                    # Add metadata columns to standardised table
                    sparkTableDF = sparkDF.withColumn("ingested_datetime", current_timestamp()) \
                                          .withColumn("expected_from", lit(expected_from)) \
                                          .withColumn("expected_to", lit(expected_to))

                    # Reorder metadata columns for the standardised delta table
                    sparkTableDF = reorder_columns(sparkTableDF)

                    delta_table_path = f"abfss://odw-standardised@{storage_account}{Param_Json_SchemaFolder_Name}/{definition['Standardised_Table_Name']}"
                    full_table_name = f"{database_name}.{definition['Standardised_Table_Name']}"
                    schema_path = f"abfss://odw-config@{storage_account}{definition['Standardised_Table_Definition']}"

                    # ENHANCED: Strict schema validation - JSON schema is the ultimate truth
                    expected_schema_fields = [f['name'] for f in json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))['fields']]
                    actual_fields = sparkTableDF.columns

                    # Remove metadata columns from comparison as they're always added by the system
                    metadata_columns = {"ingested_datetime", "expected_from", "expected_to"}
                    actual_business_fields = set(actual_fields) - metadata_columns
                    expected_business_fields = set(expected_schema_fields) - metadata_columns

                    missing_columns = expected_business_fields - actual_business_fields
                    extra_columns = actual_business_fields - expected_business_fields

                    # STRICT VALIDATION: CSV must match JSON schema exactly
                    if missing_columns:
                        logError(f"CSV file missing required columns from JSON schema for table {definition['Standardised_Table_Name']}: {', '.join(missing_columns)}")
                        logInfo(f"Skipping ingestion for {definition['Standardised_Table_Name']} due to missing required columns.")
                        result_entry["error_message"] = f"Schema validation failed - CSV missing required columns: {', '.join(missing_columns)}"
                        result_entry["end_exec_time"] = str(datetime.now())
                        result_entry["total_exec_time"] = str(time_diff_seconds(start_exec_time, datetime.now()))
                        processing_results.append(result_entry)
                        file_index += 1
                        continue

                    if extra_columns:
                        logError(f"CSV file has additional columns not defined in JSON schema for table {definition['Standardised_Table_Name']}: {', '.join(extra_columns)}")
                        logError(f"JSON schema is the ultimate truth. CSV file must match JSON schema exactly.")
                        logInfo(f"Skipping ingestion for {definition['Standardised_Table_Name']} due to extra columns in CSV.")
                        result_entry["error_message"] = f"Schema validation failed - CSV has additional columns not in JSON schema: {', '.join(extra_columns)}"
                        result_entry["end_exec_time"] = str(datetime.now())
                        result_entry["total_exec_time"] = str(time_diff_seconds(start_exec_time, datetime.now()))
                        processing_results.append(result_entry)
                        file_index += 1
                        continue

                    # If we reach here, CSV exactly matches JSON schema
                    logInfo(f"CSV schema validation passed for {definition['Standardised_Table_Name']} - CSV matches JSON schema exactly.")

                    # ENHANCED: Use JSON schema compliance for all files
                    write_success = write_dataframe_with_json_schema_compliance(
                        sparkTableDF, delta_table_path, full_table_name, schema_path, 
                        is_first_file=(not table_created)
                    )

                    if not write_success:
                        raise Exception(f"Failed to write data using comprehensive schema handling")

                    # Mark table as created after first successful write
                    if not table_created:
                        table_created = True

                    # Count rows for validation
                    rows_raw = sparkDF.count()

                    end_exec_time = str(datetime.now())

                    # Update json result entry with success data
                    result_entry["record_count"] = rows_raw  # Individual file record count
                    result_entry["table_result"] = "success"
                    result_entry["end_exec_time"] = end_exec_time
                    result_entry["total_exec_time"] = str(time_diff_seconds(start_exec_time, end_exec_time))

                    logInfo(f"Successfully processed file {file_name} with {rows_raw} rows for {definition['Standardised_Table_Name']}")

                except Exception as e:

                    #Code added to capture meaningful error message
                    full_trace = traceback.format_exc()

                    table_error_msg = str(e)

                    complete_msg = table_error_msg + "\n" + full_trace
                    error_text = complete_msg[:300]           

                    # Find the position of the last full stop before 300 characters
                    last_period_index = error_text.rfind('.')

                    # Use up to the last full stop, if found; else fall back to 300 chars
                    if last_period_index != -1:
                        error_message = error_text[:last_period_index + 1] 
                    else:
                        error_message = error_text

                    logError(f"Failed processing file {file_name} for {definition['Standardised_Table_Name']} - {e}")

                    result_entry["error_message"] = f"Failed processing file {file_name} - {error_message}"
                    result_entry["end_exec_time"] = str(datetime.now())
                    result_entry["total_exec_time"] = str(time_diff_seconds(result_entry["start_exec_time"], datetime.now()))

                # Add result to the json dump
                processing_results.append(result_entry)
                file_index += 1

        else:
            # Original single file processing
            result_entry = {
                "delta_table_name": definition['Standardised_Table_Name'],
                "csv_file_name": definition.get('matched_file', 'unknown_file'),
                "record_count": 0,
                "table_result": "failed",
                "start_exec_time": "",
                "end_exec_time": "",
                "total_exec_time": "",
                "error_message": ""
            }

            try:
                start_exec_time = str(datetime.now())
                result_entry["start_exec_time"] = start_exec_time

                # Original single file processing
                sparkDF = read_file(f"{definition['latest_path']}{definition['matched_file']}")

                if sparkDF is None:
                    logError(f"No data loaded for: {definition['Standardised_Table_Name']}")
                    continue

                expected_from = datetime.now() - timedelta(days=definition['Expected_Within_Weekdays'])
                expected_to = datetime.now()

                # Clean column names if not already done in multi-file processing
                sparkDF = clean_column_names(sparkDF)

                # Add metadata columns to standardised table
                sparkTableDF = sparkDF.withColumn("ingested_datetime", current_timestamp()) \
                                      .withColumn("expected_from", lit(expected_from)) \
                                      .withColumn("expected_to", lit(expected_to))

                # Reorder metadata columns for the standardised delta table
                sparkTableDF = reorder_columns(sparkTableDF)

                delta_table_path = f"abfss://odw-standardised@{storage_account}{Param_Json_SchemaFolder_Name}/{definition['Standardised_Table_Name']}"
                full_table_name = f"{database_name}.{definition['Standardised_Table_Name']}"
                schema_path = f"abfss://odw-config@{storage_account}{definition['Standardised_Table_Definition']}"

                # ENHANCED: Strict schema validation - JSON schema is the ultimate truth
                expected_schema_fields = [f['name'] for f in json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))['fields']]
                actual_fields = sparkTableDF.columns

                # Remove metadata columns from comparison as they're always added by the system
                metadata_columns = {"ingested_datetime", "expected_from", "expected_to"}
                actual_business_fields = set(actual_fields) - metadata_columns
                expected_business_fields = set(expected_schema_fields) - metadata_columns

                missing_columns = expected_business_fields - actual_business_fields
                extra_columns = actual_business_fields - expected_business_fields

                # STRICT VALIDATION: CSV must match JSON schema exactly
                if missing_columns:
                    logError(f"CSV file missing required columns from JSON schema for table {definition['Standardised_Table_Name']}: {', '.join(missing_columns)}")
                    logInfo(f"Skipping ingestion for {definition['Standardised_Table_Name']} due to missing required columns.")
                    result_entry["error_message"] = f"Schema validation failed - CSV missing required columns: {', '.join(missing_columns)}"
                    result_entry["end_exec_time"] = str(datetime.now())
                    result_entry["total_exec_time"] = str(time_diff_seconds(start_exec_time, datetime.now()))
                    processing_results.append(result_entry)
                    continue

                if extra_columns:
                    logError(f"CSV file has additional columns not defined in JSON schema for table {definition['Standardised_Table_Name']}: {', '.join(extra_columns)}")
                    logError(f"JSON schema is the ultimate truth. CSV file must match JSON schema exactly.")
                    logInfo(f"Skipping ingestion for {definition['Standardised_Table_Name']} due to extra columns in CSV.")
                    result_entry["error_message"] = f"Schema validation failed - CSV has additional columns not in JSON schema: {', '.join(extra_columns)}"
                    result_entry["end_exec_time"] = str(datetime.now())
                    result_entry["total_exec_time"] = str(time_diff_seconds(start_exec_time, datetime.now()))
                    processing_results.append(result_entry)
                    continue

                # If we reach here, CSV exactly matches JSON schema
                logInfo(f"CSV schema validation passed for {definition['Standardised_Table_Name']} - CSV matches JSON schema exactly.")

                # ENHANCED: Write using JSON schema compliance
                write_success = write_dataframe_with_json_schema_compliance(
                    sparkTableDF, delta_table_path, full_table_name, schema_path, is_first_file=True
                )

                if not write_success:
                    raise Exception(f"Failed to write data using strategy: {write_strategy}")

                # Count rows for validation
                rows_raw = sparkDF.count()
                standardised_table_df = spark.read.format("delta").load(delta_table_path)
                rows_new = standardised_table_df.filter(
                    (col("expected_from") == expected_from) & 
                    (col("expected_to") == expected_to)
                ).count()

                end_exec_time = str(datetime.now())

                # Update json result entry with success data
                result_entry["record_count"] = standardised_table_df.count()  # Total records in delta table
                result_entry["table_result"] = "success"
                result_entry["end_exec_time"] = end_exec_time
                result_entry["total_exec_time"] = str(time_diff_seconds(start_exec_time, end_exec_time))

                if rows_raw <= rows_new:
                    logInfo(f"All rows successfully written to {definition['Standardised_Table_Name']} — Raw: {rows_raw}, Written: {rows_new}")
                else:
                    logError(f"Mismatch in row count for {definition['Standardised_Table_Name']}: expected {rows_raw}, got {rows_new}")

            except Exception as e:

                #Code added to capture meaningful error message
                full_trace = traceback.format_exc()

                table_error_msg = str(e)

                complete_msg = table_error_msg + "\n" + full_trace
                error_text = complete_msg[:300]           

                # Find the position of the last full stop before 300 characters
                last_period_index = error_text.rfind('.')

                # Use up to the last full stop, if found; else fall back to 300 chars
                if last_period_index != -1:
                    error_message = error_text[:last_period_index + 1] 
                else:
                    error_message = error_text

                logError(f"Failed processing for {definition['Standardised_Table_Name']} - {e}")

                result_entry["error_message"] = f"Failed processing for {definition['Standardised_Table_Name']} - {error_message} "
                result_entry["end_exec_time"] = str(datetime.now())
                result_entry["total_exec_time"] = str(time_diff_seconds(result_entry["start_exec_time"], datetime.now()))

            # Add result to the json dump
            processing_results.append(result_entry)

# Execute main process
# --- Run the main process ---
process_definitions(Param_File_Load_Type)

# Prepare and return JSON result
json_result = {
    "processing_summary": {
        "total_tables_processed": len(processing_results),
        "successful_overwrites": len([r for r in processing_results if r["table_result"] == "success"]),
        "failed_overwrites": len([r for r in processing_results if r["table_result"] == "failed"])
    },
    "table_details": processing_results
}

# Convert to JSON string
result_json_str = json.dumps(json_result, indent=2, default=datetime_handler)

# Exit with the JSON result for pipeline consumption
mssparkutils.notebook.exit(result_json_str)
