import logging
import pyodbc
import azure.functions as func

def main(req: func.HttpRequest) -> func.HttpResponse:
    pipeline_name = req.params.get('pipelineName')
    if not pipeline_name:
        return func.HttpResponse(
            "Please pass pipelineName on the query string or in the request body",
            status_code=400
        )

    conn = pyodbc.connect("Driver={ODBC Driver 17 for SQL Server};Server=tcp:<your-server-name>.database.windows.net,1433;Database=<your-database-name>;Uid=<your-username>;Pwd=<your-password>;Encrypt=yes;TrustServerCertificate=no;Connection Timeout=30;")
    cursor = conn.cursor()
    cursor.execute("SELECT ActivityName, Status FROM PipelineCheckpoints WHERE PipelineName = ?", pipeline_name)
    rows = cursor.fetchall()
    cursor.close()
    conn.close()

    checkpoints = {row.ActivityName: row.Status for row in rows}
    return func.HttpResponse(body=json.dumps(checkpoints), mimetype="application/json")
