@logging_to_appins
def test_table_exists(db_name: str, table_name: str) -> bool:
    spark.sql(f"USE {db_name}")
    tables_df: DataFrame = spark.sql("SHOW TABLES")
    table_names: list = [row['tableName'] for row in tables_df.collect()]
    return table_name in table_names

if test_table_exists(db_name, table_name):
    logInfo(f"{db_name}.{table_name} Table already exists, exiting")
    mssparkutils.notebook.exit('')
else:
    logInfo(f"{db_name}.{table_name} Table does not already exist")

@logging_to_appins
def create_spark_schema(db_name: str, entity_name: str) -> StructType:
    incremental_key: str = get_incremental_key(entity_name, storage_account, path_to_orchestration_file) if db_name == 'odw_harmonised_db' else None
    schema = mssparkutils.notebook.run("/py_create_spark_schema", 30, {"db_name": db_name, "entity_name": entity_name, "incremental_key": incremental_key, "is_servicebus_schema": is_servicebus_schema})
    spark_schema = StructType.fromJson(json.loads(schema))
    return spark_schema

@logging_to_appins
def get_table_metadata(db_name: str, table_name: str) -> (str, str):
    """
    Extracts table_location and table_type given a database and table name.
    """
    filtered_df = df_exploded.filter(
        (col("database_name") == db_name) & (col("table_name") == table_name)
    ).select("table_location", "table_type")

    # Collect and return the result
    result = filtered_df.collect()
    if result:
        return result[0]["table_location"], result[0]["table_type"]
    else:
        return None, None

@logging_to_appins
def create_spark_dataframe() -> DataFrame:
    spark_dataframe: DataFrame = spark.createDataFrame([], schema=create_spark_schema(db_name, entity_name))
    return spark_dataframe

@logging_to_appins
def create_spark_table(db_name: str, table_name: str, spark_dataframe: DataFrame) -> None:
    spark_dataframe.write.format("delta").saveAsTable(f"{db_name}.{table_name}")
    logInfo(f"{db_name}.{table_name} Table created")

@logging_to_appins
def get_container_name(db_name: str) -> str:
    if db_name == 'odw_standardised_db':
        return 'odw-standardised'
    if db_name == 'odw_harmonised_db':
        return 'odw-harmonised'
    if db_name == 'odw_curated_db':
        return 'odw-curated'
