def list_all_files(base_path, relative_path=""):
    files = []
    full_path = f"{base_path}/{relative_path}" if relative_path else base_path

    try:
        items = mssparkutils.fs.ls(full_path)
    except Exception as e:
        print(f"Warning: Could not access path '{full_path}': {str(e)}")
        return full_path  # Skip this path and continue

    for item in items:
        item_path = f"{relative_path}/{item.name}".rstrip("/")
        if item.isDir:
            files.extend(list_all_files(base_path, item_path))
        else:
            files.append(item_path)

    return set(files)

for file in delta_file_list:
    try:
        container_name = file.split("@")[0].split("//")[1]
        base_path = file.split("'")[1]
        backup_path = base_path.replace(source_storage_account_path, backup_storage_account_path).replace(container_name, "delta-backup-container")

        # Try to list files in base path
        try:
            base_path_lst = list_all_files(base_path)
        except Exception as e:
            print(f"Error listing files at base path '{base_path}': {e}")
            continue

        # Create the backup directory
        try:
            mssparkutils.fs.mkdirs(backup_path)
        except Exception as e:
            print(f"Error creating directory at backup path '{backup_path}': {e}")
            continue

        # Try to list files in backup path
        try:
            backup_path_lst = list_all_files(backup_path)
        except Exception as e:
            print(f"Error listing files at backup path '{backup_path}': {e}")
            continue

        # Identify delta files
        delta_files = set(base_path_lst) - set(backup_path_lst)
        print(f"{len(delta_files)} new delta files found for backup.")

        # Copy each delta file
        for f in delta_files:
            try:
                # mssparkutils.fs.cp(base_path + f, backup_path + f)
                print(f"Copied file: {base_path + f} to {backup_path + f}")
            except Exception as e:
                print(f"Error copying file '{f}': {e}")

    except Exception as e:
        print(f"Unexpected error processing file '{file}': {e}")
