def rebuild_table(db_name: str, table_name: str, table_location: str, table_format: str, table_type: str):
    try:
        # Validate parameters
        if not db_name or db_name == "Unknown":
            raise ValueError("Database name not provided or is of value 'Unknown'.")
        if not table_name or table_name == "Unknown":
            raise ValueError("Table name not provided or is of value 'Unknown'.")
        if not table_location or table_location == "Unknown":
            raise ValueError("Location not provided or is of value = 'Unknown'.")
        if not table_format or table_format == "Unknown":
            raise ValueError("Table format not provided or is of value 'Unknown'.")
        if not table_type or table_type == "Unknown":
            raise ValueError("Table type not provided or is of value 'Unknown'.")

        # Create or rebuild the table
        spark.sql(f"""CREATE TABLE IF NOT EXISTS {db_name}.{table_name}
        USING {table_format}
        LOCATION '{table_location}'
        """)
        print(f"Table {db_name}.{table_name} rebuilt at location\n{table_location}")
    except ValueError as ve:
        print(f"Validation error: {ve}")
    except Exception as e:
        print(f"Failed to rebuild table {db_name}.{table_name}: {e}")
