%%sql

SELECT COUNT (DISTINCT PA) AS Source_Number_PAreas FROM odw_standardised_db.vw_saphr;
SELECT DISTINCT PA AS Source_PAreas FROM odw_standardised_db.vw_saphr;
SELECT COUNT (DISTINCT PersonnelAreaCode)  AS Current_Number_PAreas FROM odw_harmonised_db.personnelarea_dim;
SELECT DISTINCT PersonnelAreaCode AS Current_PAreas FROM odw_harmonised_db.personnelarea_dim;
SELECT COUNT (DISTINCT PersonnelAreaCode) AS New_Data_Number FROM personnelarea_dim_new WHERE NewData = 'Y';
SELECT DISTINCT PersonnelAreaCode AS New_Data_Records FROM personnelarea_dim_new WHERE NewData = 'Y';
SELECT COUNT (DISTINCT PersonnelAreaCode) AS Deleted_Data_Number FROM personnelarea_dim_new WHERE DeletedData = 'Y';
SELECT COUNT (DISTINCT PersonnelAreaCode) AS Deleted_Data_Records FROM personnelarea_dim_new WHERE DeletedData = 'Y';
SELECT DISTINCT PA AS Existing_PAreas FROM odw_standardised_db.vw_saphr T1 RIGHT JOIN odw_harmonised_db.personnelarea_dim T7 ON T1.PA = T7.PersonnelAreaCode;
SELECT * FROM personnelarea_dim_new;
