@logging_to_appins
def get_schema_from_url(url: str) -> dict:
    try:
        response = requests.get(url)
        if response.status_code == 200:
            data = response.json()
            return data
        else:
            logError(f"Failed to fetch data from URL. Status code:{response.status_code}")
    except requests.exceptions.RequestException as e:
        logException(e)

@logging_to_appins
def get_spark_type(field_schema: dict) -> dict:
    json_type: str = field_schema.get('type')
    
    if isinstance(json_type, list):
        json_type = json_type[0]  # Consider the first type in the list for simplicity

    if json_type == 'array':
        element_schema = field_schema['items']
        element_type = get_spark_type(element_schema)
        return ArrayType(element_type)
    elif json_type == 'object':
        return transform_schema(field_schema)
    elif json_type in type_mapping:
        return type_mapping[json_type]
    else:
        logException(e)
        raise ValueError(f"Unsupported type: {json_type}")

@logging_to_appins
def transform_schema(schema: dict) -> StructType:
    fields: list = []
    for field_name, field_schema in schema['properties'].items():
        if field_schema != {}:
            spark_type = get_spark_type(field_schema)
            field_nullable = 'null' in field_schema.get('type', []) if isinstance(field_schema.get('type'), list) else False
            fields.append(StructField(field_name, spark_type, field_nullable))
    return StructType(fields)

@logging_to_appins
def resolve_refs(schema: dict, definitions: dict) -> dict:
    if isinstance(schema, dict):
        if '$ref' in schema:
            ref = schema['$ref']
            ref_path = ref.split('/')
            if ref_path[0] == '#' and ref_path[1] == '$defs':
                return resolve_refs(definitions[ref_path[2]], definitions)
        return {k: resolve_refs(v, definitions) for k, v in schema.items()}
    elif isinstance(schema, list):
        return [resolve_refs(item, definitions) for item in schema]
    else:
        return schema

@logging_to_appins
def add_master_columns_to_schema(db_name: str) -> StructType:
    if db_name == "odw_standardised_db":
        master_fields: StructType = standardised_fields
        all_fields: list = spark_schema.fields + master_fields.fields

    elif db_name == "odw_harmonised_db":
        master_fields: StructType = harmonised_fields
        all_fields: list = spark_schema.fields + master_fields.fields
        if incremental_key_field:
            all_fields.insert(0, incremental_key_field.fields[0])

    elif db_name == "odw_curated_db" or db_name == "odw_curated_migration_db":
        all_fields: list = spark_schema.fields

    final_schema = StructType(all_fields)

    return final_schema
