faker = Faker()

def generate_unique_uuids(existing_ids, count):
    new_uuids = []
    while len(new_uuids) < count:
        new_id = faker.unique.uuid4()
        if new_id not in existing_ids:
            new_uuids.append(new_id)
            existing_ids.add(new_id)
    return new_uuids

def generate_random_value(field_name: str, field_type):
    """Generate a random value based on the field's data type."""
    if field_name == "message_type":
        return "Create"
    if field_name == "message_id":
        return faker.unique.uuid4()
    if field_name == "IsActive":
        return random.choice(["Y"])
    if field_name == "caseReference":
        return f"TR{random.randint(1, 10**6)}"
    if isinstance(field_type, StringType):
        return faker.word()
    elif isinstance(field_type, LongType):
        return random.randint(1, 10**9)
    elif isinstance(field_type, IntegerType):
        return random.randint(1, 10**5)
    elif isinstance(field_type, DoubleType):
        return random.uniform(1.0, 1000.0)
    elif isinstance(field_type, FloatType):
        return random.uniform(1.0, 100.0)
    elif isinstance(field_type, TimestampType):
        return datetime.now() - timedelta(seconds=random.randint(0, 86400 * 365))
    elif isinstance(field_type, DateType):
        return datetime.now().date() - timedelta(days=random.randint(0, 365))
    elif isinstance(field_type, BooleanType):
        return random.choice([True, False])
    else:
        return None

def generate_random_row(schema):
    """Generate a random row of data based on the schema."""
    return {field.name: generate_random_value(field.name, field.dataType) for field in schema.fields}

def generate_random_dataframe(schema: StructType, num_records: int):
    """Generate a PySpark DataFrame with random data based on the schema."""
    # Generate random data
    data = [generate_random_row(schema) for _ in range(num_records)]

    # Create DataFrame
    return spark.createDataFrame(data, schema=schema)
