from pyspark.sql import SparkSession
from notebookutils import mssparkutils
import datetime

file_extension = '.xlsx'
container_name = 'odw-raw'
relative_path = 'Fileshare/SAP_HR/Casework'

storage_account = mssparkutils.notebook.run('/utils/py_utils_get_storage_account')

'''
    Function to move excel files which end with date to their respective dated folders

    Args:
    None

    Returns:
    None

    Actions:
    Moves excel files which end with a date to their respective dated folders.

    Exceptions:
    None

'''

def move_files_in_dated_folders():

    folders = mssparkutils.fs.ls("abfss://"+container_name+"@"+storage_account+relative_path)

    for folder in folders:

        if not folder.isDir or folder.name not in folders_to_check:
            continue

        files = mssparkutils.fs.ls(folder.path)

        for file in files:
            if not file.isFile or not is_excel(file.name):
                continue

            # getting last 8 characters before the extension to extract date
            date = file.name[len(file_extension) * -1 - 8:len(file_extension) * -1]

            if is_valid_date(date):
                date_formatted = format_date(date)
                dest_dir = "{}/{}/{}".format(folder.path, date_formatted, file.name)
                mssparkutils.fs.mv(file.path, dest_dir, True, overwrite=True)




def is_excel(file):
    return file.endswith(file_extension)


def is_valid_date(date):
    try:
        datetime.date.fromisoformat(format_date(date))
        return True
    except:
        return False


def format_date(date):
    return datetime.datetime.strptime(date, '%Y%m%d').strftime('%Y-%m-%d')
