@LoggingUtil.logging_to_appins
def get_schema_from_url(url: str) -> dict:
    import requests
    try:
        response = requests.get(url)
        if response.status_code == 200:
            data = response.json()
            return data
        else:
            print("Failed to fetch data from URL. Status code:", response.status_code)
    except requests.exceptions.RequestException as e:
        print("Error fetching data:", e)

@LoggingUtil.logging_to_appins
def get_type(column: dict) -> str:
    if 'type' not in column:
        return 'string'
    elif isinstance(column['type'], list):
        column['type'] = column['type'][0]
        return get_type(column)
    elif column['type'] == 'integer':
        return 'long'
    else:
        return column['type']

@LoggingUtil.logging_to_appins
def convert_to_datalake_schema(schema: dict) -> dict:
    data_model: dict = {'fields': []}

    for key in schema['properties'].keys():
        value: dict = schema['properties'][key]
        data_model['fields'].append({
            'metadata': {},
            'name': key,
            # type doesn't exist for all fields, hence adding this check
            'type': get_type(value),
            'nullable': 'type' not in value or 'null' in value.get('type', [])
        })

    if db_name == 'odw_standardised_db':
        data_model['fields'].extend([
            {
                'metadata': {},
                'name': 'ingested_datetime',
                'type': 'timestamp',
                'nullable': False
            },
            {
                'metadata': {},
                'name': 'expected_from',
                'type': 'timestamp',
                'nullable': False
            },
            {
                'metadata': {},
                'name': 'expected_to',
                'type': 'timestamp',
                'nullable': False
            },
            {
                'metadata': {},
                'name': 'message_id',
                'type': 'string',
                'nullable': False
            },
            {
                'metadata': {},
                'name': 'message_type',
                'type': 'string',
                'nullable': False
            },
            {
                'metadata': {},
                'name': 'message_enqueued_time_utc',
                'type': 'string',
                'nullable': False
            }
        ])

    elif db_name == 'odw_harmonised_db':

        if incremental_key:
            data_model['fields'].insert(0, {
                'metadata': {},
                'name': incremental_key,
                'type': 'string',
                'nullable': False
            })

        data_model['fields'].extend([
            {
                'metadata': {},
                'name': 'Migrated',
                'type': 'string',
                'nullable': False
            },
            {
                'metadata': {},
                'name': 'ODTSourceSystem',
                'type': 'string',
                'nullable': True
            },
            {
                'metadata': {},
                'name': 'SourceSystemID',
                'type': 'string',
                'nullable': True
            },
            {
                'metadata': {},
                'name': 'IngestionDate',
                'type': 'string',
                'nullable': True
            },
            {
                'metadata': {},
                'name': 'ValidTo',
                'type': 'string',
                'nullable': True
            },
            {
                'metadata': {},
                'name': 'RowID',
                'type': 'string',
                'nullable': True
            },
            {
                'metadata': {},
                'name': 'IsActive',
                'type': 'string',
                'nullable': True
            }
        ])

    return data_model
