#https://github.com/Azure/azure-sdk-for-python/blob/main/sdk/monitor/azure-monitor-opentelemetry-exporter/samples/logs/sample_log.py
import logging
import functools
import uuid
from pyspark.sql import SparkSession
from notebookutils import mssparkutils
from opentelemetry.sdk._logs import LoggerProvider, LoggingHandler
from opentelemetry._logs import get_logger_provider, set_logger_provider
from opentelemetry.sdk._logs.export import BatchLogRecordProcessor
from azure.monitor.opentelemetry.exporter import AzureMonitorLogExporter
import threading

logger = logging.getLogger()
for h in list(logger.handlers):
    if isinstance(h, LoggingHandler):
        logger.removeHandler(h)

logger_provider = LoggerProvider()
LOGGING_INITIALIZED = globals().get("LOGGING_INITIALIZED", False)

pipelinejobid = (
    mssparkutils.runtime.context['pipelinejobid']
    if mssparkutils.runtime.context.get('isForPipeline', False)
    else uuid.uuid4()
)

def logInfo(msg: str): logger.info(f"{pipelinejobid} : {msg}")
def logError(msg: str): logger.error(f"{pipelinejobid} : {msg}")
def logException(ex: Exception): logger.exception(f"{pipelinejobid} : {ex}")

@retry(wait=wait_exponential(multiplier=1, min=2, max=10), stop=stop_after_delay(20), reraise=True, before_sleep=before_sleep)
def setupLogging(force=False):
    global LOGGING_INITIALIZED

    if LOGGING_INITIALIZED and not force:
        logInfo("Logging already initialized.")
        return

    try:
        key = mssparkutils.credentials.getSecretWithLS("ls_kv", "application-insights-connection-string")
        conn_string = key.split(";")[0]
    except Exception as e:
        print("Failed to get connection string:", e)
        return

    set_logger_provider(logger_provider)
    exporter = AzureMonitorLogExporter.from_connection_string(conn_string)
    logger_provider.add_log_record_processor(
        BatchLogRecordProcessor(exporter, schedule_delay_millis=5000)
    )

    if not any(isinstance(h, LoggingHandler) for h in logger.handlers):
        logger.addHandler(LoggingHandler())

    if not any(isinstance(h, logging.StreamHandler) for h in logger.handlers):
        logger.addHandler(logging.StreamHandler())

    logger.setLevel(logging.INFO)
    LOGGING_INITIALIZED = True
    logInfo("Logging initialized.")

def flushLogging(timeout: int = 60):
    def flushLoggingInner():
        try:
            logger_provider.force_flush()
        except Exception as e:
            print(f"Flush failed: {e}")
    t = threading.Thread(target=flushLogging)
    t.start()
    t.join(timeout)
    if t.is_alive():
        print(f"Logging flush timed out in py_logging_decorator. Waited {timeout} seconds")

def shutdownLogging():
    try:
        logger_provider.shutdown()
    except Exception as e:
        print(f"Shutdown failed: {e}")

def logging_to_appins(func):
    @functools.wraps(func)
    def wrapper(*args, **kwargs):
        args_repr = [repr(a) for a in args]
        kwargs_repr = [f"{k}={v!r}" for k, v in kwargs.items()]
        logInfo(f"Function {func.__name__} called with args: {', '.join(args_repr + kwargs_repr)}")
        try:
            return func(*args, **kwargs)
        except mssparkutils.handlers.notebookHandler.NotebookExit as e:
            logInfo(f"Notebook exited: {e}")
            mssparkutils.notebook.exit(e)
        except Exception as e:
            logException(e)
            raise
    return wrapper

if not LOGGING_INITIALIZED:
    setupLogging()
    flushLogging()

if __name__ == "__main__":
    @logging_to_appins
    def testException():
        raise Exception("Test Exception")

    try:
        logInfo("Running testException")
        testException()
    except Exception as e:
        logError("Caught error")
        flushLogging()

if __name__ == "__main__":
    @logging_to_appins
    def test():
        raise ValueError('BANANA')

    try:
        logInfo("Running test")
        test()
    except Exception:
        logError("Caught error")
        flushLogging()

if __name__ == "__main__":
    @logging_to_appins
    def test2():
        raise ValueError('CARROT')

    try:
        logInfo("Running test2")
        test2()
    except Exception:
        logError("Caught error")
        flushLogging()

if __name__ == "__main__":
    @logging_to_appins
    def test3():
        raise ValueError('CAULIFLOWER')

    try:
        logInfo("Running test3")
        test3()
    except Exception:
        logError("Caught error")
        flushLogging()
