# Import modules
from delta.tables import DeltaTable



standardised_storage_delta_table_path = standardised_storage_container + '/' + standardised_storage_delta_folder + '/' + standardised_storage_delta_table_name

sourceParquetDF = spark.read.option("inferSchema","true").format('parquet').option("recursiveFileLookup", "true").load(workspace_storage_folder_path)

if (DeltaTable.isDeltaTable(spark,standardised_storage_delta_table_path)):
    deltaTable = DeltaTable.forPath(spark,standardised_storage_delta_table_path)
    # Merge new data into existing table
    deltaTable.alias("existing").merge(
        source=sourceParquetDF.alias("updates"),
        condition="existing." + delta_lake_table_key_column + " = updates." + delta_lake_table_key_column  # We look for matches on the name column
    ).whenMatchedUpdateAll(
    ).whenNotMatchedInsertAll(
    ).execute()



else:
    sourceParquetDF.write.format('delta').save(standardised_storage_delta_table_path)
    spark.sql("CREATE TABLE {0}.{1} USING DELTA LOCATION '{2}'".format(delta_lake_database_name,delta_lake_table_name,standardised_storage_delta_table_path))
