def get_creation_date(file_path):
    statinfo = os.stat(file_path)
    modified_date = datetime.fromtimestamp(statinfo.st_mtime)
    return datetime.fromtimestamp(statinfo.st_ctime)


def get_latest_file(source_path):
    jobId = mssparkutils.env.getJobId()
    mssparkutils.fs.unmount("/temp_raw") 
    mssparkutils.fs.mount(source_path, "/temp_raw", {"linkedService":"ls_storage"})   

    path = f"/synfs/{jobId}/temp_raw/"
    fpaths = [path + fd for fd in os.listdir(path)]

    return max(fpaths, key=get_creation_date).replace(path, '')
