USE_MAX_DATE_FILTER = False

if USE_MAX_DATE_FILTER:
    try:
        logInfo("Getting the maximum file date")
        table_df = spark.table(table_name)
        max_extracted_date = get_max_file_date(df=table_df)
        print(f"max_extracted_date: {max_extracted_date}")
    except Exception as e:
        logError(f"Error getting the maximum file date:\n{e}")

    try:
        logInfo("Getting the filtered list of paths")
        filtered_paths = extract_and_filter_paths(get_all_files_recursive(source_path=source_path), max_extracted_date)
        df = read_raw_messages(filtered_paths=filtered_paths)
    except Exception as e:
        logError(f"Error getting the filtered list of paths:\n{e}")
else:
    try:
        logInfo("Getting list of missing files")
        missing_files = get_missing_files(table_name, source_path)
        df = read_raw_messages(filtered_paths=missing_files)
    except Exception as e:
        logError(f"Error getting list of missing files:\n{e}")

if not test_rows_appended(new_table_row_count=new_table_row_count, expected_row_count=expected_new_count):
    logError("Error appending rows. The new row count isn't as expected.")
else:
    logInfo("Rows appended successfully.")
