def insert_rows_in_df(source_df: DataFrame, target_df: DataFrame, incremental_key: str) -> DataFrame:
    # appending the incremental key
    max_id: int = target_df.count()

    w: Window = Window.orderBy(lit(1))
    source_df: DataFrame = source_df.withColumn(incremental_key, (max_id + row_number().over(w)).cast("string"))

    return target_df.union(source_df)

if create_df.count() > 0:
    target_df = insert_rows_in_df(create_df, target_df, hrm_incremental_key)

print(f"New rows: {create_df.count()}\nTarget dataframe: {target_df.count()}")

if delete_df.count() > 0:

    for row in delete_df.collect():
        mask = (target_df['IsActive'] == 'Y') & (target_df[entity_primary_key] == row[entity_primary_key])
        target_df = target_df.withColumn('ValidTo', when(mask, row['IngestionDate']).otherwise(target_df['ValidTo']))
        target_df = target_df.withColumn('IsActive', when(mask, 'N').otherwise(target_df['IsActive']))

print(f"Deleted rows: {delete_df.count()}\nTarget dataframe: {target_df.count()}")
