@logging_to_appins
def convert_json_array_to_csv(json_obj):
    def process_json(obj):
        if isinstance(obj, dict):
            for key, value in obj.items():
                obj[key] = process_json(value)
            return obj
        elif isinstance(obj, list):
            return ', '.join(obj)
        else:
            return obj

    return process_json(json_obj)

if date_folder == '':    
    date_folder = datetime.now().date().strftime('%Y-%m-%d')

source_path = f"{raw_container}{source_folder}/{source_frequency_folder}/{date_folder}"

try:
    files = mssparkutils.fs.ls(source_path)
except Exception as e:
    logInfo(f"No new data")
    mssparkutils.notebook.exit(False)

files = [f for f in files if f.name.endswith('.json')]

data = []

for file in files:
    logInfo(f"Processing: {file.name}")
    df = spark.read.json(file.path)
    try:
        json_data = [json.loads(d) for d in df.toJSON().collect()]

        # some entities have nested objects and have special requirements when converting to csv
        if source_frequency_folder == 'nsip-exam-timetable':
            json_data_new = []

            for d in json_data:
                for e in d['events']:
                    for key, value in d.items():
                        if not isinstance(value, dict) and not isinstance(value, list):
                            e[key] = d[key]
                    json_data_new.append(e)

            json_data = json_data_new
        # some entities have nested objects and have special requirements when converting to csv
        if source_frequency_folder == 'appeal-has':
            json_data_new = []

            for d in json_data:
                for e in d['neighbouringSiteAddresses']:
                    for key, value in d.items():
                        if not isinstance(value, dict) and not isinstance(value, list):
                            e[key] = d[key]
                    json_data_new.append(e)

            json_data = json_data_new

        json_data_parsed = [convert_json_array_to_csv(d) for d in json_data]
        data.extend(json_data_parsed)
    except Exception as e:
        logError(f"Unable to process {file.name}: {str(e)}")

if len(data) == 0:
    logInfo('No new data')
    mssparkutils.notebook.exit(False)
