def merge_schema(table_name, table_df, in_current_not_in_existing, in_existing_not_in_current):

    # adding new columns in the existing table
    for item in in_current_not_in_existing:
        print(f"Adding new column '{item[0]}' of type '{item[1]}' in {table_name} since it's not in the table's schema")
        table_df = table_df.withColumn(item[0], lit(None).cast(item[1]))

    # removing columns from the existing table that are not in the new df
    for item in in_existing_not_in_current:
        print(f"Removing column '{item[0]}' from {table_name} since it's not in the table's schema")
        table_df = table_df.drop(item[0])

    apply_df_to_table(table_df, table_name.split('.')[0], table_name.split('.')[1])
