import hashlib

date_output_format = '%Y-%m-%d %H:%M:%S'

def calculate_md5(row):
    row_str = ''.join(map(str, row))
    md5_hash = hashlib.md5(row_str.encode()).hexdigest()
    return md5_hash

def apply_mapping(df):
    output_df = pd.DataFrame()
    for target_col, source_col in mapping.items():
        output_df['IngestionDate'] = df['message_enqueued_time_utc'].apply(fix_date_format)
        if source_col in df.columns:
            if 'Date' in target_col:
                output_df[target_col] = pd.to_datetime(df[source_col])
            else:
                output_df[target_col] = df[source_col].astype(str)
        else:
            output_df[target_col] = source_col

    output_df['RowID'] = output_df.apply(calculate_md5, axis=1)

    source_system_df = spark.sql(f"SELECT * FROM {hrm_db}.main_sourcesystem_fact WHERE Description = 'Casework' AND IsActive = 'Y'")
    output_df['SourceSystemID'] = source_system_df.toPandas().at[0, 'SourceSystemID']

    output_df['Migrated'] = '1'
    output_df['ODTSourceSystem'] = 'ODT'
    output_df['ValidTo'] = None
    output_df['IsActive'] = 'Y'
    output_df[hrm_incremental_key] = ''

    return output_df

def insert_rows_in_df(source_df, target_df, incremental_key):
    # appending the incremental key
    max_id = int(target_df[incremental_key].astype(int).max())
    for index, row in source_df.iterrows():
        max_id += 1
        source_df.loc[index, incremental_key] = str(max_id)

    return pd.concat([target_df, source_df], ignore_index=True)

def fix_date_format(date_str):
    date_str = datetime.strptime(date_str, '%Y-%m-%dT%H:%M:%S.%f%z').strftime(date_output_format)
    return datetime.strptime(date_str, date_output_format)

