    schema = mssparkutils.notebook.run('/py_get_schema_from_url', 30, {'db_name': hrm_db, 'entity_name': entity_name, 'incremental_key': hrm_incremental_key})
    if schema:
        create_table_from_schema(schema, hrm_db, hrm_table, harmonised_container, hrm_table)   
    else:
        logError(f"Cannot create table {hrm_table} since the Schema for {entity_name} not found.")
        raise ValueError(f"Cannot create table {hrm_table} since the Schema for {entity_name} not found.")
else:
