import hashlib

@logging_to_appins
def calculate_md5(row):
    row_str = ''.join(map(str, row))
    md5_hash = hashlib.md5(row_str.encode()).hexdigest()
    return md5_hash

@logging_to_appins
def cast_dtypes(source_df: pd.DataFrame, target_df: pd.DataFrame):
    for source_column, source_dtype in source_df.dtypes.items():
        for target_column, target_dtype in target_df.dtypes.items():
            if source_column.lower() == target_column.lower() and source_dtype != target_dtype:

                print('Source column:' + str(source_column) + ', data type:' + str(source_dtype))
                print('Target column:' + str(target_column) + ', data type:' + str(target_dtype))

                source_df[source_column] = source_df[source_column].replace(['None'], '')

                if target_dtype == 'bool':
                    source_df[source_column] = source_df[source_column].map({'False': False, 'True': True})
                else:
                    source_df[source_column] = source_df[source_column].astype(target_dtype)


@logging_to_appins
def apply_mapping(df: pd.DataFrame, target_df: pd.DataFrame) -> pd.DataFrame:
    output_df = pd.DataFrame()

    for target_col, source_col in mapping.items():
        output_df['IngestionDate'] = df['message_enqueued_time_utc']
        if source_col in df.columns:
            output_df[target_col] = df[source_col].astype(str)
        else:
            output_df[target_col] = None

    output_df['RowID'] = output_df.apply(calculate_md5, axis=1)

    source_system_df = spark.sql(f"SELECT * FROM {hrm_db}.main_sourcesystem_fact WHERE Description = 'Casework' AND IsActive = 'Y'")
    output_df['SourceSystemID'] = source_system_df.toPandas().at[0, 'SourceSystemID']

    output_df['Migrated'] = '1'
    output_df['ODTSourceSystem'] = 'ODT'
    output_df['ValidTo'] = None
    output_df['IsActive'] = 'Y'
    output_df[hrm_incremental_key] = ''

    cast_dtypes(output_df, target_df)
    return output_df

@logging_to_appins
def insert_rows_in_df(source_df: pd.DataFrame, target_df: pd.DataFrame, incremental_key) -> pd.DataFrame:
    # appending the incremental key
    max_id = int(target_df[incremental_key].astype(int).max()) if len(target_df) > 0 else 0
    for index, row in source_df.iterrows():
        max_id += 1
        source_df.loc[index, incremental_key] = str(max_id)

    cast_dtypes(source_df, target_df)
    return pd.concat([target_df, source_df], ignore_index=True)
