# Import modules
from delta.tables import DeltaTable



curated_storage_delta_table_path = curated_storage_container + '/' + curated_storage_delta_folder + '/' + curated_storage_delta_table_name

sourceParquetDF = spark.read.option("inferSchema","true").format('parquet').option("recursiveFileLookup", "true").load(workspace_storage_folder_path)

if (DeltaTable.isDeltaTable(spark,curated_storage_delta_table_path)):
    deltaTable = DeltaTable.forPath(spark,curated_storage_delta_table_path)
    # Merge new data into existing table
    deltaTable.alias("existing").merge(
        source=sourceParquetDF.alias("updates"),
        condition="existing." + delta_lake_table_key_column + " = updates." + delta_lake_table_key_column  # We look for matches on the name column
    ).whenMatchedUpdateAll(
    ).whenNotMatchedInsertAll(
    ).execute()



else:
    sourceParquetDF.write.format('delta').save(curated_storage_delta_table_path)
    spark.sql("CREATE TABLE {0} USING DELTA LOCATION '{1}'".format(delta_lake_table_name,curated_storage_delta_table_path))
