def test_hzn_and_curated_count_matched(hzn_table: str, curated_table: str, hzn_primary_key: str, cur_primary_key: str = '') -> bool: 

    if cur_primary_key == '':
        cur_primary_key = hzn_primary_key

    df_hzn: DataFrame = spark.sql(f"""
                                SELECT DISTINCT t1.{hzn_primary_key}
                                FROM odw_standardised_db.{hzn_table} t1
                                INNER JOIN odw_standardised_db.horizon_nsip_data t2
                                ON t1.caseReference = t2.caseReference
                                WHERE t1.expected_from = (
                                        SELECT MAX(expected_from)
                                        FROM odw_standardised_db.{hzn_table}
                                    )
                                AND t2.expected_from = (
                                        SELECT MAX(expected_from)
                                        FROM odw_standardised_db.horizon_nsip_data
                                    )
                            """)

    df_cur: DataFrame = spark.sql(f"""
                                SELECT DISTINCT {cur_primary_key}
                                FROM odw_curated_migration_db.{curated_table}
                            """)

    return df_hzn.count() == df_cur.count()

