def test_curated_row_count(hrm_table_final: str, curated_table: str, data_model_columns: list) -> tuple[int, int, bool]:
    hrm_table_full: str = f"{hrm_db_name}.{hrm_table_final}"
    curated_table_full: str = f"{curated_db_name}.{curated_table_name}"
    
    hrm_df: DataFrame = spark.sql(f"select * from {hrm_table_full} where IsActive = 'Y'").drop_duplicates()
    
    if data_model_columns:
        hrm_df = hrm_df.select(data_model_columns).drop_duplicates()
        
    hrm_count: int = hrm_df.count()

    curated_count: int = spark.table(curated_table_full).count()
    
    return (hrm_count, curated_count, hrm_count == curated_count)

def test_std_to_hrm_no_dropping_records(std_table: str, hrm_table: str) -> bool:
    df: DataFrame = spark.sql(f"""
    select documentId
    from {std_db_name}.{std_table}
    where documentId not in
    (
        select documentId
        from {hrm_db_name}.{hrm_table}
    )
    """)

    return df.count() == 0

