def test_sb_std_to_sb_hrm_no_dropping_records(sb_std_table: str, sb_hrm_table: str) -> bool:
    df: DataFrame = spark.sql(f"""
    select documentId
    from {std_db_name}.{sb_std_table}
    where documentId not in
    (
        select documentId
        from {hrm_db_name}.{sb_hrm_table}
    )
    """)

    return df.count() == 0

def test_sb_hrm_to_hrm_final_no_dropping_records(sb_hrm_table: str, hrm_table_final: str) -> bool:
    df: DataFrame = spark.sql(f"""
    select documentId
    from {hrm_db_name}.{sb_hrm_table}
    where documentId not in
    (
        select documentId
        from {hrm_db_name}.{hrm_table_final}
    )
    """)

    return df.count() == 0

def test_hrm_to_curated_no_dropping_records(hrm_table_final: str, curated_table_name: str) -> bool:
    df: DataFrame = spark.sql(f"""
    select documentId
    from {hrm_db_name}.{hrm_table_final}
    where IsActive = 'Y'
    and documentId not in
    (
        select documentId
        from {curated_db_name}.{curated_table_name}
    )
    """)

    return df.count() == 0

def test_horizon_to_hrm_final_no_dropping_records(horizon_std_table_name: str, hrm_table_final: str) -> bool:
    df: DataFrame = spark.sql(f"""
    select dataId
    from {std_db_name}.{horizon_std_table_name}
    where ingested_datetime = (select max(ingested_datetime) from {std_db_name}.{horizon_std_table_name})
    and dataId not in
    (
        select documentId
        from {hrm_db_name}.{hrm_table_final}
    )
    """)

    return df.count() == 0

