df_hzn: DataFrame = spark.sql(f"""
                            SELECT DISTINCT t1.{primary_key}
                            FROM odw_standardised_db.{std_hzn_table_name} t1
                            INNER JOIN odw_standardised_db.horizon_nsip_data t2
                            ON t1.caseReference = t2.caseReference
                            WHERE t1.expected_from = (
                                    SELECT MAX(expected_from)
                                    FROM odw_standardised_db.{std_hzn_table_name}
                                )
                            AND t2.expected_from = (
                                    SELECT MAX(expected_from)
                                    FROM odw_standardised_db.horizon_nsip_data
                                )
                        """)

df_cur: DataFrame = spark.sql(f"""
                            SELECT DISTINCT {primary_key}
                            FROM odw_curated_migration_db.{curated_migration_table_name}
                        """)


missing = df_hzn.subtract(df_cur)
display(missing)
