def inspector_sb_std_to_sb_hrm_no_dropping_records(sb_std_table: str, sb_hrm_table: str, primary_key: str) -> bool:
    df: DataFrame = spark.sql(f"""
    select {primary_key}
    from {std_db_name}.{sb_std_table}
    where {primary_key} not in
    (
        select {primary_key}
        from {hrm_db_name}.{sb_hrm_table}
    ) 
    """)
    return df.count() == 0
