def test_horizon_std_to_hrm_no_dropping_records(std_table_name: str, hrm_table_name: str) -> bool:
    df: DataFrame = spark.sql(f"""
    select lpaName
    from {std_db_name}.{std_table_name}
    where lpaName not in
    (
        select lpaName
        from {hrm_db_name}.{hrm_table_name}
    )
    """)

    return df.count() == 0

def test_hrm_to_curated_no_dropping_records(hrm_table_name: str, curated_table_name: str) -> bool:
    df: DataFrame = spark.sql(f"""
    select LPAId
    from {hrm_db_name}.{hrm_table_name}
    where IsActive = 'Y'
    and LPAId not in
    (
        select LPAId
        from {curated_db_name}.{curated_table_name}
    )
    """)

    return df.count() == 0
