def check_count_match(hzn_table: str, hzn_table_2: str) -> bool: 
    df_hzn_1: Dataframe = spark.sql(f"""
                                SELECT DISTINCT caseuniqueid
                                FROM odw_standardised_db.{hzn_table}
                            """)

    df_hzn_2: Dataframe = spark.sql(f"""
                                SELECT DISTINCT caseReference
                                FROM odw_standardised_db.{hzn_table_2}
                                
                            """)

    return df_hzn_1.count() == df_hzn_2.count()

