# Function to check if filename has date pattern
@logging_to_appins
def has_date_pattern_in_filename(filename):
    """Check if filename contains a date pattern"""
    date_patterns = [
        r'_\d{8}',           # _YYYYMMDD
        r'_\d{4}-\d{2}-\d{2}', # _YYYY-MM-DD
        r'_\d{4}_\d{2}_\d{2}', # _YYYY_MM_DD
        r'\d{8}',            # YYYYMMDD --anywhere in filename
        r'\d{4}-\d{2}-\d{2}', # YYYY-MM-DD --anywhere in filename
        r'\d{4}_\d{2}_\d{2}'  # YYYY_MM_DD --anywhere in filename
    ]
    
    for pattern in date_patterns:
        if re.search(pattern, filename):
            return True
    return False

# Function to find files with same base name but different dates
@logging_to_appins
def find_multi_date_files(files, filename_start):
    """Find files that have the same base name but different dates"""
    matching_files = []
    base_files = []
    
    for file in files:
        if file.startswith(filename_start):
            if has_date_pattern_in_filename(file):
                matching_files.append(file)
            else:
                base_files.append(file)
    
    # If we found files with date patterns, use those for multi-file processing
    if len(matching_files) > 1:
        return matching_files, 'multi_file'
    elif len(matching_files) == 1:
        return matching_files, 'single_file'
    elif len(base_files) == 1:
        # Fallback to single file without date pattern
        return base_files, 'single_file'
    elif len(base_files) > 1:
        # Multiple files without clear date pattern - take the first one
        return [base_files[0]], 'single_file'
    else:
        return [], 'none'

# Function to extract date from filename with various date formats
@logging_to_appins
def extract_date_from_filename(filename):
    """Extract date from filename supporting multiple date formats"""
    try:
        # Pattern for YYYYMMDD format (8 digits)
        date_pattern_8 = r'(\d{8})'
        # Pattern for YYYY-MM-DD format
        date_pattern_dash = r'(\d{4}-\d{2}-\d{2})'
        # Pattern for YYYY_MM_DD format
        date_pattern_underscore = r'(\d{4}_\d{2}_\d{2})'
        
        match_8 = re.search(date_pattern_8, filename)
        match_dash = re.search(date_pattern_dash, filename)
        match_underscore = re.search(date_pattern_underscore, filename)
        
        if match_8:
            date_str = match_8.group(1)
            return datetime.strptime(date_str, '%Y%m%d')
        elif match_dash:
            date_str = match_dash.group(1)
            return datetime.strptime(date_str, '%Y-%m-%d')
        elif match_underscore:
            date_str = match_underscore.group(1)
            return datetime.strptime(date_str, '%Y_%m_%d')
        else:
            logInfo(f"No date found in filename: {filename}")
            return datetime.min
    except Exception as e:
        logError(f"Error extracting date from filename {filename}: {e}")
        return datetime.min

# Function to check schema compatibility and determine write mode
@logging_to_appins
def determine_write_strategy(spark_df, delta_table_path, schema_path, table_name):
    """Determine the appropriate write strategy based on schema compatibility"""
    try:
        # Load expected schema from JSON
        schema_json = json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))
        expected_fields = {f['name'].lower(): f['type'] for f in schema_json["fields"]}
        
        # Get actual DataFrame schema (excluding metadata columns we always add)
        excluded_columns = {'ingested_datetime', 'expected_from', 'expected_to'}
        actual_fields = {field.name.lower(): str(field.dataType) for field in spark_df.schema.fields if field.name.lower() not in excluded_columns}
        
        # Check if Delta table exists
        table_exists = DeltaTable.isDeltaTable(spark, delta_table_path)
        
        if not table_exists:
            logInfo(f"Delta table does not exist for {table_name}. Will create new table.")
            return "create_new", None
        
        # If table exists, get its current schema (excluding old tracking columns)
        try:
            current_table_df = spark.read.format("delta").load(delta_table_path)
            # Exclude old tracking columns and metadata columns from comparison
            excluded_columns = {'source_file', 'file_processed_datetime', 'ingested_datetime', 'expected_from', 'expected_to'}
            current_fields = {field.name.lower(): str(field.dataType) for field in current_table_df.schema.fields if field.name.lower() not in excluded_columns}
        except Exception as e:
            logError(f"Error reading existing table schema: {e}")
            return "create_new", None
        
        # Compare only business data columns (not metadata or tracking columns)
        missing_in_current = set(actual_fields.keys()) - set(current_fields.keys())
        missing_in_actual = set(current_fields.keys()) - set(actual_fields.keys())
        
        if missing_in_current and not missing_in_actual:
            logInfo(f"New business columns detected in source data: {missing_in_current}")
            logInfo(f"Will use MERGE schema option for {table_name}")
            return "merge_schema", missing_in_current
        elif not missing_in_current and not missing_in_actual:
            logInfo(f"Business schema matches exactly for {table_name}. Will use standard overwrite/append.")
            return "overwrite", None
        elif missing_in_actual and not missing_in_current:
            logInfo(f"Some columns missing in source data for {table_name}: {missing_in_actual}")
            logInfo(f"Will use standard overwrite/append - missing columns will be null.")
            return "overwrite", None
        else:
            logInfo(f"Schema differences detected for {table_name}")
            logInfo(f"Missing in current table: {missing_in_current}")
            logInfo(f"Missing in source data: {missing_in_actual}")
            logInfo(f"Will use standard overwrite/append")
            return "overwrite", None
            
    except Exception as e:
        logError(f"Error determining write strategy for {table_name}: {e}")
        return "overwrite", None

# Function to check if JSON schema has evolved vs existing Delta table
@logging_to_appins
def check_json_schema_evolution(delta_table_path, schema_path, table_name):
    """Check if JSON schema has evolved compared to existing Delta table schema"""
    try:
        # Check if Delta table exists
        table_exists = DeltaTable.isDeltaTable(spark, delta_table_path)
        
        if not table_exists:
            logInfo(f"Delta table does not exist for {table_name}. Will create new table.")
            return False, "create_new"
        
        # Get JSON schema
        schema_json = json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))
        expected_fields = set([f['name'] for f in schema_json["fields"]])
        
        # Get existing table schema
        try:
            existing_table_df = spark.read.format("delta").load(delta_table_path)
            # Remove metadata columns from comparison
            excluded_columns = {"ingested_datetime", "expected_from", "expected_to", "source_file", "file_processed_datetime"}
            existing_business_fields = set(existing_table_df.columns) - excluded_columns
            expected_business_fields = expected_fields - excluded_columns
            
            # Check for schema differences between JSON and existing table
            if existing_business_fields != expected_business_fields:
                missing_in_table = expected_business_fields - existing_business_fields
                missing_in_json = existing_business_fields - expected_business_fields
                
                logInfo(f"JSON schema evolution detected for {table_name}:")
                logInfo(f"  Columns in JSON schema but not in existing table: {missing_in_table}")
                logInfo(f"  Columns in existing table but not in JSON schema: {missing_in_json}")
                
                if missing_in_json:
                    # Columns have been removed from JSON schema - need schema reset
                    logInfo(f"JSON schema has removed columns. Will use schema reset for {table_name}")
                    return True, "schema_reset"
                elif missing_in_table:
                    # New columns added to JSON schema - can use merge schema
                    logInfo(f"JSON schema has new columns. Will use merge schema for {table_name}")
                    return False, "merge_schema"
            
            logInfo(f"JSON schema matches existing table exactly for {table_name}. Will use standard overwrite.")
            return False, "standard"
            
        except Exception as e:
            logError(f"Error reading existing table schema for {table_name}: {e}")
            logInfo(f"Will reset schema due to read error")
            return True, "schema_reset"
            
    except Exception as e:
        logError(f"Error checking JSON schema evolution for {table_name}: {e}")
        return False, "standard"

# Function to reorder DataFrame columns to match JSON schema order
def reorder_dataframe_to_match_schema(spark_df, schema_path, table_name):
    """Reorder DataFrame columns to exactly match JSON schema order"""
    try:
        # Get JSON schema column order
        schema_json = json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))
        expected_columns = [f['name'] for f in schema_json["fields"]]
        
        # Get current DataFrame columns
        current_columns = spark_df.columns
        
        # Check if reordering is needed
        if current_columns == expected_columns:            
            return spark_df
               
        # Reorder columns to match JSON schema
        reordered_df = spark_df.select(*expected_columns)
        return reordered_df
        
    except Exception as e:
        logError(f"Error reordering DataFrame for {table_name}: {e}")
        return spark_df

# Function to write DataFrame based on JSON schema evolution
@logging_to_appins
def write_dataframe_with_json_schema_compliance(spark_df, delta_table_path, full_table_name, schema_path, is_first_file=True):
    """Write DataFrame with JSON schema compliance - JSON schema is the ultimate truth"""
    try:
        # Ensure DataFrame column order matches JSON schema BEFORE any processing
        spark_df = reorder_dataframe_to_match_schema(spark_df, schema_path, full_table_name)
        
        needs_reset, strategy = check_json_schema_evolution(delta_table_path, schema_path, full_table_name)
        
        # Always check if column order matches for any schema evolution
        table_exists = DeltaTable.isDeltaTable(spark, delta_table_path)
        force_schema_reset = False
        
        if table_exists and strategy != "create_new":
            try:
                existing_df = spark.read.format("delta").load(delta_table_path)
                existing_columns = [field.name for field in existing_df.schema.fields]
                new_columns = [field.name for field in spark_df.schema.fields]
                
                # Check if column order is different
                if existing_columns != new_columns:
                    force_schema_reset = True
            except Exception as e:
                logInfo(f"Error checking column order: {e}")
        
        # Always use overwriteSchema for any schema changes
        if needs_reset or strategy == "schema_reset" or force_schema_reset or strategy == "merge_schema":
            # Any schema change requires complete table reset
            create_table_if_not_exists(delta_table_path, full_table_name, schema_path)
            spark_df.write.format("delta").mode("overwrite").option("overwriteSchema", "true").save(delta_table_path)
            if strategy == "merge_schema":
                logInfo(f"Overwrote with schema evolution maintaining exact JSON schema order: {full_table_name}")
            else:
                logInfo(f"Reset Delta table schema to match exact JSON schema order: {full_table_name}")
            
        elif strategy == "create_new":
            # New table - create based on JSON schema
            create_table_if_not_exists(delta_table_path, full_table_name, schema_path)
            spark_df.write.format("delta").mode("overwrite").save(delta_table_path)
            logInfo(f"Created new Delta table based on JSON schema: {full_table_name}")
            
        else:
            # Standard write (strategy == "standard") - but check column order
            if force_schema_reset:
                spark_df.write.format("delta").mode("overwrite").option("overwriteSchema", "true").save(delta_table_path)
                logInfo(f"Overwrote Delta table to fix column order to match JSON schema: {full_table_name}")
            elif is_first_file:
                spark_df.write.format("delta").mode("overwrite").save(delta_table_path)
                logInfo(f"Overwrote Delta table (JSON schema unchanged): {full_table_name}")
            else:
                spark_df.write.format("delta").mode("append").save(delta_table_path)
                logInfo(f"Appended to Delta table (JSON schema unchanged): {full_table_name}")
        
        # Verify the final table schema after write
        try:
            final_df = spark.read.format("delta").load(delta_table_path)
            logInfo(f"Final table schema after write for {full_table_name}:")
                        
            # Check if the column order now matches JSON schema
            final_columns = [field.name for field in final_df.schema.fields]
            df_columns = [field.name for field in spark_df.schema.fields]
            
            # Also get JSON schema order for comparison
            try:
                schema_json = json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))
                json_schema_columns = [f['name'] for f in schema_json["fields"]]
                
                if final_columns == json_schema_columns:
                    logInfo(f"Column order SUCCESS - table order matches JSON schema order exactly")
                else:
                    logInfo(f"Column order MISMATCH with JSON schema")
                    logInfo(f"JSON schema order: {json_schema_columns}")
                    logInfo(f"Table order: {final_columns}")
            except Exception as e:
                logInfo(f"Could not verify JSON schema order: {e}")
            
            # Check if all columns are present
            final_column_set = set(final_columns)
            df_column_set = set(df_columns)
            
            if df_column_set.issubset(final_column_set):
                logInfo(f"Schema evolution SUCCESS - all DataFrame columns present in final table")
            else:
                missing = df_column_set - final_column_set
                logInfo(f"Schema evolution FAILED - missing columns in final table: {missing}")
                
        except Exception as e:
            logError(f"Could not verify final table schema: {e}")
        
        return True
        
    except Exception as e:
        logError(f"Error writing DataFrame to {full_table_name}: {e}")
        return False

@logging_to_appins
def read_file(file_path):
    try:
        if file_path.endswith(".csv"):
            return spark.read.option("header", True).csv(file_path)
			
        elif file_path.endswith(".xlsx"):
            return spark.read.format("com.crealytics.spark.excel") \
                             .option("header", "true") \
                             .option("inferSchema", "true") \
                             .load(file_path)        
        else:
            raise Exception("Unsupported file format")
    except Exception as e:
        logError(f"Failed to load file {file_path}: {e}")
        return None

def clean_column_names(df):
    cols = [re.sub(r"[^a-zA-Z0-9_]+", "", c).strip('_') for c in df.columns]
    deduped = []
    for i, c in enumerate(cols):
        count = cols[:i].count(c)
        deduped.append(f"{c}{count + 1}" if count else c)
    return df.toDF(*deduped)

# Reorder dataframe columns to bring additional metadata columns to the front
def reorder_columns(df):
    """Reorders the columns so that metadata columns come first."""
    try:
        metadata_cols = ["ingested_datetime", "expected_from", "expected_to"]
        remaining_cols = [col for col in df.columns if col not in metadata_cols]
        return df.select(metadata_cols + remaining_cols)
    except Exception as e:
        logError(f"Error reordering columns: {e}")
        return df

@logging_to_appins
def create_table_if_not_exists(path, table_name, schema_path):
    try:
        if not DeltaTable.isDeltaTable(spark, path):
            schema_json = json.loads("".join([r.value for r in spark.read.text(schema_path).collect()]))
            schema_str = ", ".join([f"{f['name']} {f['type']}" for f in schema_json["fields"]])
            spark.sql(f"CREATE TABLE IF NOT EXISTS {table_name} ({schema_str}) USING DELTA LOCATION '{path}'")
    except Exception as e:
        logError(f"Error creating table {table_name}: {e}")

@logging_to_appins
def time_diff_seconds(start, end):
    try:
        if not start or not end:
            return 0

        # Parse strings into datetime objects if needed
        if isinstance(start, str):
            start = datetime.strptime(start, "%Y-%m-%d %H:%M:%S.%f")
        if isinstance(end, str):
            end = datetime.strptime(end, "%Y-%m-%d %H:%M:%S.%f")

        diff_seconds = int((end - start).total_seconds())
        return diff_seconds if diff_seconds > 0 else 0

    except Exception as e:
        return 0

#This funtion handles datetime object and covert into string
def datetime_handler(obj):
    if isinstance(obj, datetime):
        return obj.isoformat()
    raise TypeError(f"Object of type {obj.__class__.__name__} is not JSON serializable")

# Prepare and return JSON result
json_result = {
    "processing_summary": {
        "total_tables_processed": len(processing_results),
        "successful_overwrites": len([r for r in processing_results if r["table_result"] == "success"]),
        "failed_overwrites": len([r for r in processing_results if r["table_result"] == "failed"])
    },
    "table_details": processing_results
}

# Convert to JSON string
result_json_str = json.dumps(json_result, indent=2, default=datetime_handler)
