def create_json_from_table_examination_timetable(data: pd.DataFrame) -> list:

    """
    Args:
        data: pandas dataframe

    Returns:
        list of dictionaries
    """

    data_dict = defaultdict(dict)

    event_fields = ["eventId", "type", "eventTitle", "description", "eventDeadlineStartDate", "date"]
    eventLineItemFields = ["description"]

    date_format = "%Y-%m-%d %H:%M:%S"

    for index, row in data.iterrows():
        caseReference = row["caseReference"]

        for column, value in row.items():
            if caseReference not in data_dict:
                data_dict[caseReference][column] = value

                data_dict[caseReference]["events"] = []

            events = data_dict[caseReference]["events"]
            new_event = {column: value for column, value in row.items() if column in event_fields}
            new_eventLineItem = {column: value for column, value in row.items() if column in eventLineItemFields}
            
            if new_event not in events:
                events.append(new_event)

        for event in events:
            if "eventLineItems" not in event:
                event["eventLineItems"] = []
                eventLineItems = event["eventLineItems"]
                if new_eventLineItem not in eventLineItems:
                    eventLineItems.append(new_eventLineItem)

    final_dict = {key: dict(value) for key, value in data_dict.items()}

    data_to_compare = list(final_dict.values())

    return data_to_compare
