from pyspark.sql.functions import col
from pyspark.sql.types import StructType, StructField

def flatten_struct(df):
    # List to hold column names for the flattened DataFrame
    flat_columns = []
    
    # Iterate over the schema to find struct fields
    for field in df.schema.fields:
        if isinstance(field.dataType, StructType):
            # For each struct type field, flatten it
            for sub_field in field.dataType.fields:
                flat_columns.append(col(f"{field.name}.{sub_field.name}").alias(f"{field.name}_{sub_field.name}"))
        else:
            # For non-struct fields, just add them as they are
            flat_columns.append(col(field.name))
    
    # Return the DataFrame with flattened struct columns
    return df.select(*flat_columns)

# Example usage
df_flat = flatten_struct(df)
