#Create function to retry in the event of a read or parallel writes. We don't want a one off clash causing the pipeline to fail.
def save_to_delta_with_retry(df, table_name, max_retries):
    retries = 0
    while retries < max_retries:
        try:
            # Attempt to save the DataFrame to the Delta table
            df.write.mode("append").format("delta").saveAsTable(table_name)
            print(f"Data saved successfully to {table_name}")
            break  # Exit the loop if successful
        except Exception as e:
            print(f"Error saving data to {table_name}: {str(e)}")
            retries += 1
            if retries < max_retries:
                duration = randint(0, 100)/49.000
                time.sleep(duration)
            else:
                print(f"Max retries reached. Unable to save data to {table_name}.")
                break

