def rebuild_table(db_name: str, table_name: str, location: str, table_format: str):
    spark.sql(f"""CREATE TABLE IF NOT EXISTS {db_name}.{table_name}
    USING {table_format}
    LOCATION '{location}'
    """)
    print(f"Table {db_name}.{table_name} rebuilt at location\n{location}")

def create_table_metadata(container: str, db_name: str, tables: list) -> dict:
    tables = list_tables(db_name)
    table_metadata = {}
    for table in tables:
        try:
            table_details = spark.sql(f"DESCRIBE DETAIL {db_name}.{table}")
            table_info = table_details.collect()[0].asDict()
            table_name = table_info.get("name", table)
            table_location = table_info.get("location", "Unknown")
            # This handles cases where tables were created in the default location by mistake by not specifying location in the create table script. 
            # Therefore there were tables created in the default location. This won't be needed in future.
            if table_location.startswith('abfss://synapse'):
                location = f'abfss://{container}@{storage_account}{table_name}'
            else:
                location = table_location
            table_format = table_info.get("format", "Unknown")
            table_metadata[table_name] = {
                "location": location,
                "format": table_format
                }
        except Exception as e:
            print(f"Failed to fetch details for {table}: {e}")

    return table_metadata
