absences_table = 'odw_standardised_db.hr_absences'
absences_table_loc = "abfss://odw-standardised@" + storage_account+'HR/hr_absences'

# getting the closest expected_from date in the table based on the expected_from parameter
if expected_from != '':
    absences_expected_from = spark.sql(f"SELECT expected_from FROM {absences_table} as table_alias WHERE table_alias.expected_from <= '{expected_from}' ORDER BY table_alias.expected_from DESC LIMIT 1;")
else:
    absences_expected_from = spark.sql(f"SELECT MAX(expected_from) FROM {absences_table}")
try:
    absences_expected_from = absences_expected_from.first()[0]
except:
    absences_expected_from = ''

work_schedule = spark.read.format('delta').load("abfss://odw-standardised@" + storage_account+'HR/hr_work_schedule_rule').toPandas()
absences = spark.sql(f"select * from {absences_table} where cast(expected_from as Date) = '{absences_expected_from}'").toPandas()

if(len(absences) > 0):
    for i, row in absences.iterrows():
        absences.at[i, 'hrs'] = get_absence_hrs(float(row['days']), float(row['hrs']), row['start_date'], row['end_date'], row['work_schedule_rule'])

    absencesDF = spark.createDataFrame(absences)
    absencesDF.write.format("delta").mode("overwrite").option("replaceWhere", f"cast(expected_from as Date) = '{absences_expected_from}'").save(absences_table_loc)
