def test_keyvault_connectivity():
    exception_raised = None
    try:
        mssparkutils.credentials.getSecretWithLS("ls_kv", "application-insights-connection-string")
    except Exception as e:
        exception_raised = e
    assert not exception_raised, "Could not fetch a secret from the key vault"

def test_datalake_connectivity():
    storage_endpoint = re.search('url=https://(.+?);', mssparkutils.credentials.getFullConnectionString("ls_storage")).group(1)
    exception_raised = None
    try:
        mssparkutils.fs.mount( 
            f"abfss://odw-standardised@{storage_endpoint}AIEDocumentData//", 
            "/temp_test_datalake_connectivity", 
            {"linkedService": "ls_storage"} 
        )
    except Exception as e:
        exception_raised = e
    finally:
        mssparkutils.fs.unmount("/temp_test_datalake_connectivity")
    assert not exception_raised, f"Could not connect to the main data lake '{storage_endpoint}'"

tests_to_run = {
    symbol: eval(symbol)
    for symbol in locals()
    if symbol.startswith("test_") and callable(eval(symbol))
}
print("Running tests\n")
exception_map = dict()
for test_name, test in tests_to_run.items():
    print(f"Running test '{test_name}'")
    try:
        test()
    except Exception:
        exception_map[test_name] = traceback.format_exc()

if exception_map:
    for test_case, exception in exception_map.items():
        print(f"Test '{test_case}' failed with the below exception")
        print(exception)
        print()

    failing_tests = list(exception_map.keys())
    mssparkutils.notebook.exit(failing_tests)
mssparkutils.notebook.exit([])
