import json
import jsonschema
from jsonschema import validate
list_with_all_zendesk_dict = []
@logging_to_appins
def read_zendesk(schema_dict):
    for i in range(2918):
        if i == 2802:
            i=i+1
        else:
            with open(f"/synfs/{jobId}/zendesk_items/output_{i}.json", "r",encoding='utf-8') as big_json:
                zendesk_dict = json.load(big_json)
                try:
                    validate(instance=zendesk_dict, schema=schema_dict)
                except jsonschema.exceptions.ValidationError as err:
                    print("Schema could not validate file number:")
                    print(i)
                list_with_all_zendesk_dict.append(zendesk_dict)
    return list_with_all_zendesk_dict
zendesk_dict = read_zendesk(schema_dict)

import pyspark.sql.functions as F 
@logging_to_appins
def fields_table():
    fields = shows.select(
    "id", F.explode("fields").alias("fields")
    )

    fields_id_value = shows.select(F.col("id").alias("ticket_id"),
        F.map_from_arrays(
        F.col("fields.id"), F.col("fields.value")
        ).alias("ID_VALUE")
        )
    # fields_id_value.show()
    fields_id_value = fields_id_value.select("ticket_id",
        F.posexplode("ID_VALUE").alias("position", "id", "value")
        )
    # fields_id_value.show()
    fields_id_value.drop("position")
    spark.sql(f"drop table if exists odw_harmonised_db.zendesk_fields_id_value;")
    fields_id_value.write.format('delta').saveAsTable("odw_harmonised_db.zendesk_fields_id_value")
fields_table()

