value.value:
{
    "@type": "MessageCard",
    "@context": "http://schema.org/extensions",
    "themeColor": "@{pipeline().parameters.Colour}",
    "summary": "Pipeline status alert message​​​​",
    "sections": [
        {
            "activityTitle": "@{pipeline().parameters.Message_title}​​​​",
            "activitySubtitle": "@{pipeline().parameters.Message_subtitle}",
            "activityImage": "@{pipeline().parameters.Image}",
            "facts": [
                {
                    "name": "Pipeline RunId:",
                    "value": "@{pipeline().parameters.pipelineRunId}"
                },
                {
                    "name": "Activity name:",
                    "value": "@{pipeline().parameters.activityName}"
                },
                {
                    "name": "Activity status:",
                    "value": "@{pipeline().parameters.activityStatus}"
                },
                {
                    "name": "Execution duration (mins):",
                    "value": "@{pipeline().parameters.activityDuration}"
                },
                {
                    "name": "Message",
                    "value": "@{pipeline().parameters.activityMessage}"
                },
                {
                    "name": "Notification time (UTC):",
                    "value": "@{utcNow()}"
                },
                {
                    "name": "Data Factory name:",
                    "value": "@{pipeline().DataFactory}"
                }
            ],
            "markdown": true
        }
    ],
    "potentialAction": [
        {
            "@type": "OpenUri",
            "name": "View pipeline run",
            "targets": [
                {
                    "os": "default",
                    "uri": "@{concat('https://web.azuresynapse.net/en/monitoring/pipelineruns/',pipeline().parameters.pipelineRunId,'?workspace=/subscriptions/',pipeline().parameters.dataFactorySubscription,'/resourceGroups/',pipeline().parameters.dataFactoryResourceGroup,'/providers/Microsoft.Synapse/workspaces/',pipeline().DataFactory)}"
                }
            ]
        }
    ]
}
